(33, 55, '2022-02-03', '2022-02-03', 55, '2022-02-03 08:10:04', '2022-02-02 19:40:04', NULL, 0, 0, 'Leave  Applications', '<p>I want to tell you that my mother&rsquo;s health isn&rsquo;t good. And I want to take them to the hospital. So I request you to grant me leave for today. I will be thankful to you.</p>\r\n', NULL, 'firsthalf', 'secondhalf', '1', '1', '', '53,56', ''),
(34, 42, '2022-02-09', '2022-02-09', 42, '2022-02-09 09:11:28', '2022-02-08 20:41:28', NULL, 0, 0, 'Application for leave', '<p>Hi Sir,</p>\r\n\r\n<p>Good morning,</p>\r\n\r\n<p>Today I am going out of the station for some work and I will not be able to log in today. I request you to grant me leave for one day.</p>\r\n\r\n<p>Thanks for your consideration.</p>\r\n\r\n<p>Thanks and regards,</p>\r\n\r\n<p>Gursharan Singh</p>\r\n', NULL, 'firsthalf', 'secondhalf', '1', '3', '', '52,53', ''),
(35, 52, '2022-03-16', '2022-03-17', 52, '2022-03-12 10:30:50', '2022-03-11 22:00:50', NULL, 0, 0, 'Request Leave on 16th & 17th March 2022 - Krishna Prasad', '<p>Hi Dinesh,</p>\r\n\r\n<p>Please grant me leave on 16th and 17th March, as we are going out-of-station for a close relative daughter&#39;s saree function. I will be back on 18th and resume my work. Kindly please consider, accept and approve the leaves.</p>\r\n\r\n<p>Thanks &amp; Regards<br />\r\nKrishna Prasad&nbsp;</p>\r\n', NULL, 'firsthalf', 'secondhalf', '2', '3', '', '56', ''),
(36, 55, '2022-03-17', '2022-03-17', 55, '2022-03-17 13:28:14', '2022-03-17 00:58:14', NULL, 0, 0, 'test task', '<p>test task</p>\r\n', NULL, 'firsthalf', 'secondhalf', '1', '1', '', '55', ''),
(37, 42, '2022-04-01', '2022-04-01', 42, '2022-04-12 08:37:10', '2022-04-11 20:06:34', NULL, 0, 0, 'Travelling out of station', '<p>Hi Sir, I was out of the station this day on an emergency basis to Maharashtra, at the time this was informed to you through text message&nbsp;since&nbsp;I didn&#39;t had&nbsp;access to the system that day, I kindly request you to mark this leave.</p>\r\n\r\n<p>Thank you.</p>\r\n', NULL, 'firsthalf', 'secondhalf', '1', '3', '', '56', ''),
(38, 42, '2022-04-12', '2022-04-12', 42, '2022-04-12 08:51:41', '2022-04-11 20:21:41', NULL, 0, 0, 'Application for leave', '<p>Hi, Sir, I am Going to attend an inaugural program at the Salarjung museum which would be till the afternoon. I will be back by 1 or 2 PM, I kindly request you to consider my application.</p>\r\n\r\n<p>Thank you.</p>\r\n', NULL, 'firsthalf', 'firsthalf', '0.5', '3', '', '56', '');
